const targetPagePath = "/";         // ← page d’accueil
const currentPath = window.location.pathname;

function scrollWhenReady(id, maxWait = 3000) {
  const start = Date.now();
  const interval = setInterval(() => {
    const el = document.getElementById(id);
    if (el) {
      el.scrollIntoView({ behavior: "smooth" });
      clearInterval(interval);
    } else if (Date.now() - start > maxWait) {
      clearInterval(interval); // Stop trying after 3 seconds
    }
  }, 100); // Check every 100ms
}

if (currentPath === targetPagePath) {
  // ✅ On est déjà sur la bonne page → attendre que l'élément soit là
  scrollWhenReady(targetSectionId);
} else {
  // ❌ On est sur une autre page → on stocke et on redirige
  wwLib.globalContext.targetSection = targetSectionId;
  window.location.href = targetPagePath;
}

